import React, {useEffect, useState} from 'react';
import styles from './AvailableMeals.module.css';
import MealItem from './MealItem/MealItem';
import Card from '../UI/Card.js';





//Renders Actual List to Screen
function AvailableMeals(){

  const [meals, setMeals] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState();

  useEffect(() => {
    const fetchMeals = async () => {
      
/* WITHIN GOOGLE FIREBASE IN YOUR REALTIME DATABASE
  Add Your Own Personal Foods, Descriptions, and Prices.
  THE FORMAT WILL BE SHOWN INSIDE FIREBASE FOLDER WITHIN PROJECT
*/
      const response = await fetch('ADD_YOUR_OWN_GOOGLE_REALTIME_FIREBASE_URL_HERE/meals.json');

     
     if(!response.ok){
       throw new Error('Something went wrong!');
     }
     
     
      const responseData = await response.json();
      const loadedMeals = [];

      for (const key in responseData){
        loadedMeals.push(
          {
          id: key,
          name: responseData[key].name,
          description: responseData[key].description,
          price: responseData[key].price
          })
      }

      setMeals(loadedMeals);
      setIsLoading(false);
    };

    
      fetchMeals().catch(error => {
        setIsLoading(false);
        setHttpError(error.message);
      });

  }, []);

  if(isLoading){
    return (
    <section className={styles.MealsLoading}> 
      <p>Loading ...</p>
    </section>
    )
  }

  if(httpError){
    return (
      <section className={styles.MealsError}>
        <p> {httpError} </p>
      </section>
    )
  }

    const mealsList = meals.map(meal => 
    <MealItem 
        key={meal.id}
        id={meal.id}
        name={meal.name}
        description={meal.description}
        price={meal.price}
    />);

    return(
        <section className={styles.meals}>
         <Card> 
            <ul>
                {mealsList}
            </ul>
          </Card>
        </section>
    );
}

export default AvailableMeals;
