import React from 'react';
import stlyes from './MealItemForm.module.css';
import Input from '../../UI/Input';

//Handles Quantity of Items
function MealQuantity(){
    return(
        <form className={stlyes.form}>
        <Input label='Amount' 
          input={{
            id: 'amount',
            type: 'number',
            min: '1',
            max: '5',
            step: '1',
            defaultValue: '1'
        }}
        />
            <button>+ Add </button>
        </form>
    );
}
export default MealQuantity;
